class WordDictionary:
    def __init__(self):
        self.root = TrieNode()

    def addWord(self, word):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.is_end_of_word = True

    def search(self, word):
        return self._search_helper(word, 0, self.root)

    def _search_helper(self, word, index, node):
        if index == len(word):
            return node.is_end_of_word

        char = word[index]

        if char == '.':
            for child in node.children.values():
                if self._search_helper(word, index + 1, child):
                    return True
            return False
        else:
            if char not in node.children:
                return False
            return self._search_helper(word, index + 1, node.children[char])

def combinationSum(candidates, target):
    result = []

    def backtrack(start, combination, remaining):
        if remaining == 0:
            result.append(combination[:])
            return

        if remaining < 0:
            return

        for i in range(start, len(candidates)):
            candidate = candidates[i]

            if candidate > remaining:
                continue

            combination.append(candidate)
            backtrack(i, combination, remaining - candidate)
            combination.pop()

    backtrack(0, [], target)
    return result

class UnionFind:
    def __init__(self, n):
        self.parent = list(range(n))
        self.rank = [0] * n

    def find(self, x):
        if self.parent[x] != x:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

    def union(self, x, y):
        px, py = self.find(x), self.find(y)
        if px == py:
            return
        if self.rank[px] < self.rank[py]:
            self.parent[px] = py
        elif self.rank[px] > self.rank[py]:
            self.parent[py] = px
        else:
            self.parent[py] = px
            self.rank[px] += 1

