# Alternative DP approach
def canPartitionDP(nums, target):
    dp = [False] * (target + 1)
    dp[0] = True

    for num in nums:
        for j in range(target, num - 1, -1):
            dp[j] = dp[j] or dp[j - num]

    return dp[target]

class NumArray:
    def __init__(self, nums):
        self.n = len(nums)
        self.tree = [0] * (4 * self.n)
        self.build(nums, 1, 0, self.n - 1)

    def build(self, nums, node, start, end):
        if start == end:
            self.tree[node] = nums[start]
        else:
            mid = (start + end) // 2
            self.build(nums, 2*node, start, mid)
            self.build(nums, 2*node+1, mid+1, end)
            self.tree[node] = self.tree[2*node] + self.tree[2*node+1]

    def update(self, index, val):
        self._update(1, 0, self.n - 1, index, val)

    def _update(self, node, start, end, idx, val):
        if start == end:
            self.tree[node] = val
        else:
            mid = (start + end) // 2
            if idx <= mid:
                self._update(2*node, start, mid, idx, val)
            else:
                self._update(2*node+1, mid+1, end, idx, val)
            self.tree[node] = self.tree[2*node] + self.tree[2*node+1]

    def sumRange(self, left, right):
        return self._query(1, 0, self.n - 1, left, right)

    def _query(self, node, start, end, l, r):
        if r < start or end < l:
            return 0
        if l <= start and end <= r:
            return self.tree[node]
        mid = (start + end) // 2
        left_sum = self._query(2*node, start, mid, l, r)
        right_sum = self._query(2*node+1, mid+1, end, l, r)
        return left_sum + right_sum

def subarraySum(nums, k):
    prefix_sum_count = {0: 1}
    cumulative_sum = 0
    result = 0

    for num in nums:
        cumulative_sum += num

        # Check if there's a prefix sum that makes current subarray sum = k
        needed_sum = cumulative_sum - k
        if needed_sum in prefix_sum_count:
            result += prefix_sum_count[needed_sum]

        # Add current cumulative sum to hash table
        prefix_sum_count[cumulative_sum] = prefix_sum_count.get(cumulative_sum, 0) + 1

    return result

