def copyRandomList(head):
    if not head:
        return None

    # Create mapping from original to copy
    old_to_new = {}

    # First pass: create all nodes
    current = head
    while current:
        old_to_new[current] = ListNode(current.val)
        current = current.next

    # Second pass: set next and random pointers
    current = head
    while current:
        if current.next:
            old_to_new[current].next = old_to_new[current.next]
        if current.random:
            old_to_new[current].random = old_to_new[current.random]
        current = current.next

    return old_to_new[head]

def countComponents(n, edges):
    # Build adjacency list
    graph = [[] for _ in range(n)]
    for a, b in edges:
        graph[a].append(b)
        graph[b].append(a)

    visited = [False] * n
    components = 0

    def dfs(node):
        visited[node] = True
        for neighbor in graph[node]:
            if not visited[neighbor]:
                dfs(neighbor)

    for i in range(n):
        if not visited[i]:
            components += 1
            dfs(i)

    return components

def alienOrder(words):
    graph = {}
    in_degree = {}

    # Initialize all characters
    for word in words:
        for char in word:
            graph[char] = []
            in_degree[char] = 0

    # Build graph from adjacent word comparisons
    for i in range(len(words) - 1):
        word1, word2 = words[i], words[i + 1]
        min_len = min(len(word1), len(word2))

        for j in range(min_len):
            if word1[j] != word2[j]:
                graph[word1[j]].append(word2[j])
                in_degree[word2[j]] += 1
                break
        else:
            # Invalid: longer word before its prefix
            if len(word1) > len(word2):
                return ""

    # Topological sort using Kahn's algorithm
    queue = [char for char in in_degree if in_degree[char] == 0]
    result = []

    while queue:
        char = queue.pop(0)
        result.append(char)

        for neighbor in graph[char]:
            in_degree[neighbor] -= 1
            if in_degree[neighbor] == 0:
                queue.append(neighbor)

    return ''.join(result) if len(result) == len(graph) else ""

