            for row in range(bottom, top - 1, -1):
                result.append(matrix[row][left])
            left += 1

    return result

def canPartition(nums, target):
    n = len(nums)
    dp = [set() for _ in range(1 << n)]
    dp[0].add(0)

    for mask in range(1 << n):
        for i in range(n):
            if mask & (1 << i):
                prev_mask = mask ^ (1 << i)
                for sum_val in dp[prev_mask]:
                    dp[mask].add(sum_val + nums[i])

    for mask in range(1 << n):
        if target in dp[mask]:
            return True
    return False

class NumArray:
    def __init__(self, nums):
        self.n = len(nums)
        self.tree = [0] * (4 * self.n)
        self.build(nums, 0, 0, self.n - 1)

    def build(self, nums, node, start, end):
        if start == end:
            self.tree[node] = nums[start]
        else:
            mid = (start + end) // 2
            self.build(nums, 2 * node + 1, start, mid)
            self.build(nums, 2 * node + 2, mid + 1, end)
            self.tree[node] = self.tree[2 * node + 1] + self.tree[2 * node + 2]

    def update(self, index, val):
        self.update_helper(0, 0, self.n - 1, index, val)

    def update_helper(self, node, start, end, idx, val):
        if start == end:
            self.tree[node] = val
        else:
            mid = (start + end) // 2
            if idx <= mid:
                self.update_helper(2 * node + 1, start, mid, idx, val)
            else:
                self.update_helper(2 * node + 2, mid + 1, end, idx, val)
            self.tree[node] = self.tree[2 * node + 1] + self.tree[2 * node + 2]

    def sumRange(self, left, right):
        return self.query(0, 0, self.n - 1, left, right)

    def query(self, node, start, end, l, r):
        if r < start or end < l:
            return 0
        if l <= start and end <= r:
            return self.tree[node]
        mid = (start + end) // 2
        return self.query(2 * node + 1, start, mid, l, r) + self.query(2 * node + 2, mid + 1, end, l, r)

def copyRandomList(head):
